INSERT INTO `email` (`name`, `subject`, `body`) VALUES ('notify-registered-new-provider', 'Зарегистрирован новый поcтавщик "{{%name}}"', '<p>Ссылка для просмотра: <a href="{{%viewUrl}}" target="_blank">{{%name}}</a></p>\r\n\r\n<p>Ссылка для редактирования: <a href="{{%updateUrl}}" target="_blank">{{%name}}</a></p>\r\n\r\n<p>--<br />\r\nС уважением,<br />\r\nПрошунин Олег Николаевич<br />\r\nТел.: +7 (905) 570-73-74, +7 (909) 919-06-69</p>\r\n');
INSERT INTO `email` (`name`, `subject`, `body`) VALUES ('active-profile-provider', 'Регистрация поставщика на сайте', '<p>{{%firstname}} {{%patronymic}}, Вы успешно зарегистрированы на сайте.</p>\r\n\r\n<p>Данные для входа в личный кабинет:</p>\r\n\r\n<p>Емайл: {{%email}}</p>\r\n\r\n<p>Пароль: {{%password}}</p>\r\n\r\n<p>--<br />\r\nС уважением,<br />\r\nАдминистрация сайта.</p>\r\n');
INSERT INTO `email` (`name`, `subject`, `body`) VALUES ('notice-from-member', 'Уведомление от участника', '<p>Участник {{%name}} хочет стать поставщиком собственных товаров.</p>\r\n\r\n<p>Контактные данные:</p>\r\n\r\n<p>Телефон: {{%phone}}</p>\r\n\r\n<p>Емайл: {{%email}}</p>\r\n\r\n<p>Карточка участника: <a href="{{%viewUrl}}">{{%viewUrl}}</a></p>\r\n');
