INSERT INTO `service` (`id`, `user_id`, `visibility`, `published`, `name`, `description`, `price`, `contacts`, `member_price`) VALUES
(24, 22, 1, 1, 'Настройка UNIX-серверов, разработка сайтов и интернет магазинов', '<p><span style=\"font-family:times new roman,times,serif\"><span style=\"font-size:20px\"><strong>Настройка UNIX-серверов, разработка сайтов и интернет магазинов</strong><br />\r\n<span style=\"background-color:rgb(255, 255, 255); color:rgb(68, 68, 68)\">Место нахождение мастера: г. Железнодорожный МО</span></span></span><br />\r\n&nbsp;</p>\r\n', NULL, 'Мастер: Нaумeнкo Вячecлaв Aндрeeвич\r\nКонтактный тел. (909) 920 4692', NULL),
(25, 3, 1, 1, 'Брюки мужские зауженные классические 1шт.', '<p><span style=\"font-size:20px\">Брюки мужские зауженные классические<br />\r\nПроизводитель:&nbsp;<span style=\"background-color:rgb(255, 255, 255); color:rgb(51, 51, 51); font-family:sans-serif,arial,verdana,trebuchet ms\">&nbsp;Павловский Посад<br />\r\nПошив на заказ от 3 до 5 дней.</span></span></p>\r\n\r\n<p><span style=\"color:rgb(68, 68, 68); font-family:lucida grande,sans-serif; font-size:16.1px\">Для пошива брюк по вашему размеру необходимо снять&nbsp;мерки и выслать с заказом:</span><br />\r\n<span style=\"font-size:16px\"><strong><span style=\"color:rgb(128, 0, 0)\">1. Измерить обхват талии.</span></strong><span style=\"color:rgb(68, 68, 68); font-family:lucida grande,sans-serif\">&nbsp;На уровне талии дополнительно завязать ленту, шнурок. Сантиметровая лента должна ложиться горизонтально по талии и результат записать в половинном размере, используя буквенные обозначения: Ст.</span><br />\r\n<strong><span style=\"color:rgb(128, 0, 0)\">2. Измеряем обхват бедер.</span></strong><span style=\"color:rgb(68, 68, 68); font-family:lucida grande,sans-serif\">&nbsp;Сантиметровая лента проходит горизонтально по выпуклым частям ягодиц и смыкается на животе, учитывается выпуклость живота. Обозначается буквами Сб и записывается половинное значение.</span><br />\r\n<strong><span style=\"color:rgb(128, 0, 0)\">3. Длина брюк.</span></strong><span style=\"color:rgb(68, 68, 68); font-family:lucida grande,sans-serif\">&nbsp;Измеряется сбоку от талии до пола. Обозначается буквами Дб и записывается полностью число.</span><br />\r\n<span style=\"color:rgb(128, 0, 0); font-family:lucida grande,sans-serif\"><strong>4. Высота сидения</strong></span><span style=\"color:rgb(68, 68, 68); font-family:lucida grande,sans-serif\">. Надо сесть и по боку измерить расстояние от талии до плоскости сидения. Обозначается буквами Вс и записывается число полностью.</span></span><br />\r\n<span style=\"font-size:20px\"><span style=\"background-color:rgb(255, 255, 255); color:rgb(51, 51, 51); font-family:sans-serif,arial,verdana,trebuchet ms\">Состав тканей:<br />\r\nПосмотреть и выбрать расцветку тканей можно </span><u><span style=\"color:rgb(0, 0, 255)\"><span style=\"background-color:rgb(255, 255, 255); font-family:sans-serif,arial,verdana,trebuchet ms\">Здесь</span></span></u></span></p>\r\n', '1800.00', 'Тел. (915)123-5-444  звонить с 10 до 20 ч.', '1300.00'),
(26, 3, 1, 1, 'Настройка фортепиано, изготовление и ремонт смычковых инструментов (скрипка, альт, виолончель)', '<p><span style=\"font-family:times new roman,times,serif\"><span style=\"font-size:20px\">Настройка фортепиано, изготовление&nbsp;и ремонт<br />\r\nсмычковых инструментов (скрипка, альт, виолончель)<br />\r\nУслуги оказываю по &nbsp;Москве&nbsp;и ближнему подмосковью</span></span><br />\r\n&nbsp;</p>\r\n', NULL, 'Мастер: Волик Николай Ильич, c 10.00. до 20 ч.\r\nКонтактный тел. (916)266-2004', NULL),
(27, 3, 1, 1, 'Общеукрепляющий массаж', '<p><span style=\"font-family:times new roman,times,serif\"><span style=\"font-size:20px\">Общеукрепляющий массаж<br />\r\nМесто нахождение мастера г. Железнодорожный МО<br />\r\nМастер:&nbsp;<span style=\"background-color:rgb(249, 249, 249); color:rgb(51, 51, 51)\">Корниенко Григорий Андреевич</span><br />\r\nКонтактный тел.&nbsp;(916)&nbsp;172&nbsp;9290</span></span></p>\r\n', NULL, NULL, NULL),
